let cracked_icon = ' data:image/webp;base64,'
let mojang_icon = ' data:image/png;base64,'
let microsoft_icon = ' data:image/png;base64,'

async function fetchAccounts() {
    let accountsRaw = await fetch("/secure/authserver-rawAccounts");
    let accounts = await accountsRaw.json();

    let accountsDiv = document.querySelector("#accountsContainer > tbody");
    accountsDiv.innerHTML = accounts.reduce(((prev, acc, i) => {
        let src = cracked_icon;
        if (acc.type === 'microsoft') src = microsoft_icon;
        if (acc.type === 'mojang') src = mojang_icon;

        let nick = acc?.profile?.name??"";
        if(acc.type === 'cracked') nick = acc.username;

        return prev + `<tr>
            <td>${i}</td>
            <td>${acc.properties?.authserver_username ?? acc.username}</td>
            <td>${nick}</td>
            <td><img class="account_icon" src="${src}">${acc.type}</td>
            <td><button onclick="openAccount('${acc.properties?.authserver_username}')" class="btn btn-primary btn-block">Edit</button></td>
        </tr>`
    }), "")

    accountsDiv.innerHTML += `<tr>
            <td>-</td>
            <td><button onclick="openIframe('/secure/authserver/add','Add Account')" class="btn btn-info btn-block">Add</button></td>
        </tr>`
}

let selectedAccountType = '';

function changeAccountType(type) {
    selectedAccountType = type;
    document.querySelectorAll(".acc_prop_container").forEach(el => el.style.display = 'none');
    document.querySelectorAll(`.acc_prop_container.${type}`).forEach(el => el.style.display = '');
    document.querySelectorAll(".acc_prop_container.all").forEach(el => el.style.display = '');
}

async function addAccountDOM() {
    let json = {};
    let props = document.querySelectorAll(`.acc_prop.${selectedAccountType}`);
    props.forEach(el => {
        json[el.getAttribute("prop")] = el.value;
    })
    let props2 = document.querySelectorAll(`.acc_prop.all`);
    props2.forEach(el => {
        json[el.getAttribute("prop")] = el.value;
    })
    return await addAccount(selectedAccountType, json);
}

async function addAccount(type, props) {
    let body = props;
    body["type"] = type;

    let resp = await (await fetch('/secure/authserver-addAccount', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(body)
    })).json();

    if (resp.error && resp.errorMessage) {
        alert(resp.error + "\n" + resp.errorMessage)
    } else if (resp.error && resp.message) {
        alert(resp.error + "\n" + resp.message)
    } else {
        alert("success");
    }
}

function openIframe(url, title) {
    if (url === '') {
        document.querySelector("#iframe").src = url;
        document.querySelector("#iframe_container").style.display = 'none';
        return;
    }
    document.querySelector("#iframe").src = url;
    document.querySelector("#iframe_container").style.display = 'block';
    document.querySelector("#iframe-panel-title-text").innerText = title;
}

